import React,{ useState } from 'react';

const CostForm = () => {


    // const [name, setName] = useState('');
    // const [amount, setAmount] = useState('');
    // const [date, setDate] = useState('');

    const [userUnput , setUserInput] = useState({
        inputName: '',
        inputAmount:'',
        inputDate: '',
    })


    const nameChangeHandler = (event) => {
        setUserInput({...userUnput,
            inputName: event.target.value,
        })
        // setName(event.target.value)
    }
    const amountChangeHandler = (event) => {
        setUserInput({...userUnput,
            inputAmount: event.target.value,
        })
        // setAmount(event.target.value)
    }
    const dateChangeHandler = (event) => {
        setUserInput({...userUnput,
            inputDate: event.target.value,
        })
    }
const SubmitHandler = (event) => {
event.preventDefault();
const costData = {
    name: inputName,
    amount: inputAmount,
    date: inputDate,
}
}


return <form onSubmit={SubmitHandler}>
    <div className="new-cost__controls">
        <div className= "new-cost__control">
            <label>Название</label>
            <input type="text" onChange={nameChangeHandler}></input>
        </div>
    </div>
    <div className="new-cost__controls">
        <div className="new-cost__control">
            <label>сумма </label>
            <input onChange={amountChangeHandler} type="number" min='0.01' step='0.01'></input>
        </div>
    </div>
    <div className="new-cost__controls">
        <div className="new-cost__control">
            <label>дата</label>
            <input onChange={dateChangeHandler} type="date" min='2019-01-01' step='2022-12-31'></input>
        </div>
        
    </div>
    <div className="new-cost__actions">
            <button type="submit">добавить расход</button>
        </div>
</form>
}


export default CostForm
